mapper.select:
SELECT  GAME_ID gameId
        , GAME_NAME gameName
        , IMG_URL  img
        , PLATFORM platform
        , GENRE genre
        , SERVICE_STATUS serviceStatus
FROM    GM_GAME
WHERE   USE_YN = 'Y'

    AND    PLATFORM IN

        #{item}



    AND    GENRE = #{genre, jdbcType=VARCHAR}


    AND    SERVICE_STATUS = #{serviceStatus}


    LIMIT 	#{page.pageBegin, jdbcType=NUMERIC}
    , #{page.PAGE_SCALE, jdbcType=NUMERIC}

SELECT  COUNT(*)
FROM    GM_GAME
WHERE   USE_YN = 'Y'

    AND    PLATFORM IN

        #{item}



    AND    GENRE = #{genre}


    AND    SERVICE_STATUS = #{serviceStatus}
