mapper.select:
SELECT B.GAME_ID AS gameId , B.GAME_NAME AS gameName , MAX(A.GAME_SSHOT_IMG_URL) AS gameSshotImgUrl , B.PLATFORM AS platform , B.GENRE AS genre FROM GM_GAME_SSHOT A INNER JOIN GM_GAME B ON A.GAME_ID = B.GAME_ID WHERE A.USE_YN = 'Y' AND B.USE_YN = 'Y'
AND B.PLATFORM = #{platform, jdbcType=VARCHAR}
GROUP BY B.GAME_ID
LIMIT #{page.pageBegin, jdbcType=NUMERIC} , #{page.pageScale, jdbcType=NUMERIC}

SELECT COUNT(A.GAME_ID) FROM ( SELECT B.GAME_ID FROM GM_GAME_SSHOT A INNER JOIN GM_GAME B ON A.GAME_ID = B.GAME_ID WHERE A.USE_YN = 'Y' AND B.USE_YN = 'Y'
AND B.PLATFORM = #{platform, jdbcType=VARCHAR}
GROUP BY B.GAME_ID ) A
